package datatypesandvariables;

public class ArthimeticOperators {

	public static void main(String[] args)
	{
		int a=10;
		int b=20;
		
		//Addition
		System.out.println(a+b);
		
		
		
		//Subtraction		
		System.out.println(a-b);
		
		//Multiplication		
		System.out.println(a*b);
		
		//Division		
		System.out.println(a/b);
		
		//Remainder
		System.out.println(a%b);
		
		
		// TODO Auto-generated method stub

	}

}
