package datatypesandvariables;

public class NestedForloop {

	public static void main(String[] args) {
		for (int r = 1; r <4; r++) {
			for (int c = 1; c <=4; c++)

			{
				System.out.print("Row No.="+r+ " Col No.=" + c);
			}

			System.out.println();
			// TODO Auto-generated method stub

		}

	}
}
