package datatypesandvariables;

public class WhileLoop {

	public static void main(String[] args) 
	{
		//Initialization
		
		int i=1;
		
		while(i<10) //Condition
		
		{
			System.out.println(i);
		i++;//Increment / Decrement
		
		}
		// TODO Auto-generated method stub

	}

}
